mapper.update:
UPDATE item 
   SET item_quantity=item_quantity - #{order_quantity}
 WHERE item_idx=#{item_idx}

mapper.insert:
INSERT INTO safe
VALUES (
	   (SELECT (MAX(safe_idx),'0') FROM safe) AS safe_idx
	   ,#{ord.item_idx}
	   ,#{ord.member_id}
	   ,(SELECT shop_idx FROM shop WHERE member_id=#{ord.member_id} 
	   ,#{item_price}
	   )

INSERT INTO coin
VALUES (
		(SELECT (MAX(coin_idx),'0') FROM coin) AS coin_idx
		,#{ord.member_id}	
		,'0'		
		,#{item_price} 
		,(SELECT (coin_total-#{item_price}) FROM coin WHERE coin_idx=(select IFNULL(MAX(coin_idx),'1') from coin)) AS coin_total
		,DATE_FORMAT(now(), "%Y%m%d%H%i%s")
	   )
