mapper.update:
UPDATE member
SET member_pass = #{member.member_pass}, member_nick = #{member.member_nick}, member_email = #{member.member_email}, member_phone = #{member.member_phone}
WHERE member_id = #{sId}

mapper.select:
SELECT COUNT(coin_idx)
FROM coin
WHERE member_id = #{sId}

SELECT c.coin_total
FROM coin c
WHERE member_id = #{sId}
ORDER BY coin_idx DESC
LIMIT 1;

SELECT COUNT(coupon_idx)
FROM coupon
WHERE member_id = #{sId}

SELECT COUNT(item_idx) 
FROM reserved
WHERE member_id = #{sId}

SELECT COUNT(item_idx)
FROM basket
WHERE member_id = #{sId}
