mapper.select:
SELECT COUNT(*) itemCount
FROM item it, shop s 
WHERE s.member_id = #{member_id}

mapper.insert:
INSERT INTO item
VALUES (#{item.item_idx}
	   , (SELECT shop_idx FROM shop WHERE member_id=#{member_id})
	   , null
	   , #{item.item_title}
	   , #{item.item_content}
	   , #{item.item_price}
	   , date_format(now(), '%Y%m%d')
	   , '0'
	   , '0'
	   , 'N'
	   , '0'
	   , #{item.item_region}
	   , #{item.item_quantity}
	   )

mapper.select:
SELECT i.item_idx, g.img_name, i.item_title, i.item_price, i.item_date, i.item_status, COUNT(w.member_id), i.item_readCnt, i.item_hide FROM item AS i LEFT OUTER JOIN wish AS w ON i.item_idx = w.item_idx JOIN shop AS s ON i.shop_idx = s.shop_idx JOIN img AS g ON i.item_idx = g.item_idx WHERE s.member_id = #{member_id} AND g.img_idx='1'
AND i.item_title LIKE #{keyword}
AND i.item_status = #{sell_status}
GROUP BY i.item_idx ORDER BY i.item_idx+0 ASC

mapper.delete:
DELETE FROM item
      WHERE item_idx=#{item_idx}
        AND shop_idx = (SELECT shop_idx
                          FROM shop
                         WHERE member_id=#{member.member_id}
                       )

mapper.select:
SELECT * 
  FROM member
 WHERE member_id=#{member_id}
   AND member_pass=#{member_pass}

mapper.update:
UPDATE shop
   SET shop_img = #{shop.shop_img},
       shop_name = #{shop.shop_name},
       shop_content = #{shop.shop_content}
 WHERE shop_idx = #{shop.shop_idx}

mapper.select:
SELECT COUNT(i.item_idx)
FROM shop s, item i, star st
WHERE st.item_idx = i.item_idx
AND i.shop_idx = s.shop_idx
AND s.member_id = #{member_id}
AND st.item_score = #{i}
